static const byte* music_ptr = music1;

inline byte next_music_byte() {
  return *music_ptr++;
}

void play_music() {
  byte ch;
  byte freech = 0;
  for (ch=0; ch<NV; ch++) {
    if (voice[ch].volume) {
      byte reg = (ch<<1)&2;
      pokey[ch>>1].aud[1+reg].c = 0xa0 | --voice[ch].volume;
    } else {
      freech = ch;
    }
  }
  if (music_ptr) {
    ch = freech;
    while (cur_duration == 0) {
      byte note = next_music_byte();
      if ((note & 0x80) == 0) {
	byte reg = (ch<<1)&2;
        word period = note_table[note & 63];
        pokey[ch>>1].aud[0+reg].f = period;
        pokey[ch>>1].aud[1+reg].f = period >> 8;
        voice[ch].volume = 15;
        ch = ch ? ch-1 : NV-1;
      } else {
        if (note == 0xff)
          music_ptr = NULL;
        cur_duration = note & 63;
      }
    }
    cur_duration--;
  }
}

void start_music(const byte* music) {
  music_ptr = music;
  cur_duration == 0;
}


void main() {
  int x,y;
  Matrix m;

  music_ptr = 0;
  cur_duration = 0;
  pokey[0].audctl = 0b01111000;
  pokey[1].audctl = 0b01111000;

  frame = 0;
  
//  mat_identity(&m);
  while (1) {
    dvgreset();
    CNTR();
    //STAT(RED, 5);
    x = isin(frame/8);
    y = icos(frame/8);
    //VCTR(x, y, 2);
    SCAL(0);
    mat_identity(&m);

      mat_rotate(&m, 0, frame>>1); // x
      mat_rotate(&m, 1, 3+isin(frame>>2)>>4); // y
      mat_rotate(&m, 2, frame>>2); // z

      draw_wireframe_ortho(&tetra, &m, frame);

    
    HALT();
    dvgstart();

    frame++;
    
    if (!music_ptr) start_music(music1);
    play_music();
   
    watchdog++;
  }
}
